mapper.select:
select count(*) from pressuremonitorsys.t_product where is_removed = 0
AND product_sn = #{productSN}
AND customer_account = #{customerAccount}
AND date_format(create_time, '%y-%m-%d') >= date_format(#{startTime}, '%y-%m-%d')
AND date_format(create_time, '%y-%m-%d') <= date_format(#{endTime}, '%y-%m-%d')


WHERE is_removed = 0

    AND product_sn = #{productSN}


    AND customer_account = #{customerAccount}


    AND date_format(create_time, '%y-%m-%d') >= date_format(#{startTime}, '%y-%m-%d')


    AND date_format(create_time, '%y-%m-%d') <= date_format(#{endTime}, '%y-%m-%d')

ORDER BY create_time DESC

    limit #{pageBegin}, #{pageEnd}

mapper.update:
UPDATE pressuremonitorsys.t_product
SET is_removed  = 1,
    delete_time = now(),
    delete_by   = #{operatorName}
WHERE id = #{id}

UPDATE pressuremonitorsys.t_product
SET location    = #{location},
    update_time = now(),
    update_by   = #{operatorName}
WHERE is_removed = 0
  AND id = #{id}
